import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Skylink } from 'skylinkjs';

class RequestHelp extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
        var skylink = new Skylink();
        skylink.on('peerJoined', function(peerId, peerInfo, isSelf) {
      if(isSelf) return; // We already have a video element for our video and don't need to create a new one.
      var vid = document.createElement('video');
      vid.autoplay = true;
      vid.muted = true; // Added to avoid feedback when testing locally
      vid.id = peerId;
      document.body.appendChild(vid);
    });

        skylink.on('incomingStream', function(peerId, stream, isSelf) {
      if(isSelf) return;
      var vid = document.getElementById(peerId);
      attachMediaStream(vid, stream);
    });

        skylink.on('peerLeft', function(peerId, peerInfo, isSelf) {
      var vid = document.getElementById(peerId);
      document.body.removeChild(vid);
    });
        skylink.on('mediaAccessSuccess', function(stream) {
      var vid = document.getElementById('myvideo');
      attachMediaStream(vid, stream);
    });

          skylink.init({
            apiKey: '00228257-e1ea-4b22-baf9-c328a4bade0b',
            defaultRoom: 'Pick a room name'
            }, function() {
              skylink.joinRoom({
              audio: true,
              video: true
           });
          });

    }

    componentDidMount() {
        if(!this.props.auth.isAuthenticated) {
            this.props.history.push('/login');
        }
    }


    render() { 

        return ( 
            <div className="main-dasboard">
              <div className="container mt-5">
                <div className="card dash-main-card">
                    <video id="myvideo" controls={true} playsinline autoplay muted></video>                
                </div>
              </div>
            </div>
         );
    }
}



RequestHelp.propTypes = {
    auth: PropTypes.object.isRequired,
}

const mapStateToProps = (state) => ({
    auth: state.auth,
})

export  default connect(mapStateToProps)(RequestHelp)
